            var $otherUsersLink = $target,
                $otherUsersMenu = $j('div.j-js-grouped-users[data-linkedID=' + $otherUsersLink.attr('data-linkedID') + ']');

            $otherUsersMenu.popover({context: $otherUsersLink, destroyOnClose: false});
