jive.AttendeesApp.AttendeesSource = jive.RestService.extend(function(protect) {
	protect.init = function(options) {
		this.options = options;
		this.eventID = options.eventID;
	}
	
	this.rsvp = function(answer) {
		var promise = new jive.conc.Promise();
		var error = this.errorSaving;
		var url = jive.rest.url("/event/" + this.eventID + "/attendance/" + answer);
		
		$j.ajax({
			url: url,
			type: "POST",
			contentType: "application/json; charset=utf-8",
			success: function(data) {
				promise.emitSuccess(data);
			},
			error: this.errorCallback(promise, error)
		});
		
		return promise;
	};
	
	this.getAttendance = function() {
		var promise = new jive.conc.Promise();
		var error = this.errorFinding;
		var url = jive.rest.url("/event/" + this.eventID + "/attendance/");
		
		$j.ajax({
			url: url,
			type: "GET",
			contentType: "application/json; charset=utf-8",
			dataType: "json",
			success: function(data) {
				promise.emitSuccess(data.eventAttendance);
			},
			error: this.errorCallback(promise, error)
		});
		
		return promise;
	}
	
	this.getAllAttendees = function(status) {
		var promise = new jive.conc.Promise();
		var error = this.errorFinding;
		var url = jive.rest.url("/event/" + this.eventID + "/attendance/all/" + status);
		
		$j.ajax({
			url: url,
			type: "GET",
			contentType: "application/json; charset=utf-8",
			dataType: "json",
			success: function(data) {
				promise.emitSuccess(data);
			},
			error: this.errorCallback(promise, error)
		});
		
		return promise;
	}
});
;
jive.namespace('AttendeesApp');

jive.AttendeesApp.AttendeesView = jive.AbstractView.extend(function(protect) {
	jive.conc.observable(this);
	
	this.init = function(options) {
		var view = this;
		this.options = options;
		this.size = options.size;
		this.$rsvpContainer = options.rsvpContainer;
		
		$j(document).ready(function() {
			$j('#event-rsvp-link').click(function(e) { 
				$j("#event-rsvp-choices").popover({context: $j(this), destroyOnClose: false, putBack: true});
				e.preventDefault();
			});
			
			$j('.jive-event-rsvp-choice').click(function() {
				$j("#event-rsvp-choices").trigger('close');
			});
		
			$j('#event-rsvp-yes').click(function(e) {
				view.emitP('rsvp', options.yes).addCallback(function(attendance) {
				    if(!attendance.rsvpRejected) {
					   view.displayConfirmation(options.i18n.yesConfirmationText);
					}
					e.preventDefault();
				}).addErrback(function() {
					console.log('promise got an errback');
				});
				e.preventDefault();
			});
			
			$j('#event-rsvp-no').click(function(e) {
				view.emitP('rsvp', options.no).addCallback(function() {
					view.displayConfirmation(options.i18n.noConfirmationText);
					e.preventDefault();
				}).addErrback(function() {
					console.log('promise got an errback');
				});
				e.preventDefault();
			});
			
			$j('#event-rsvp-maybe').click(function(e) {
				view.emitP('rsvp', options.maybe).addCallback(function() {
					view.displayConfirmation(options.i18n.maybeConfirmationText);
					e.preventDefault();
				}).addErrback(function() {
					console.log('promise got an errback');
				});
				e.preventDefault();
			});
			
			if(view.size == 'large') {
    			$j('#see-all-attending-link').click(function(e) {
    				view.emitP('seeAll', options.yes, 'yes').addCallback(function() {
    					e.preventDefault();
    				}).addErrback(function() {
    					console.log('promise got an errback');
    				});
    			});
    
    			$j('#see-all-notattending-link').click(function(e) {
    				view.emitP('seeAll', options.no, 'no').addCallback(function() {
    					e.preventDefault();
    				}).addErrback(function() {
    					console.log('promise got an errback');
    				});
    			});
    			
    			$j('#see-all-tentative-link').click(function(e) {
    				view.emitP('seeAll', options.maybe, 'maybe').addCallback(function() {
    					e.preventDefault();
    				}).addErrback(function() {
    					console.log('promise got an errback');
    				});
    			});
    			
    			$j('#see-all-awaiting-link').click(function(e) {
    				view.emitP('seeAll', options.awaiting, 'awaiting').addCallback(function() {
    					e.preventDefault();
    				}).addErrback(function() {
    					console.log('promise got an errback');
    				});
    			});
    	    }
			
			if(options.rsvpRejected) {
			     setTimeout(function() {
			         $j('#jive-event-norsvp').animate({color: '#000000'}, 600);
			     }, 5000);
			}
		});
	}
	
	this.refresh = function(attendance) {
	    var attendanceView;
	    if(this.size == 'large') {
	       attendanceView = jive.event.attendee.attendanceView;
	    } else {
	       attendanceView = jive.event.attendee.attendanceViewSmall;
	    }
	
		$j(this.$rsvpContainer).html(attendanceView({
		    anonymous : attendance.anonymous,
		    containerAllowsAttendance: attendance.containerAllowsAttendance,
		    canJoinGroupToContribute: attendance.canJoinGroupToContribute,
			canAttend : attendance.canAttend,
			cannotAttendReasonKey : attendance.cannotAttendReasonKey,
			attendanceCapped : attendance.capped,
			maxAttendees : attendance.maxAttendees,
			status : attendance.response,
			yesAttendees : attendance.yesAttendees,
			noAttendees : attendance.noAttendees,
			maybeAttendees : attendance.maybeAttendees,
			awaitingAttendees : attendance.awaitingAttendees,
			yes : this.options.yes,
			no : this.options.no,
			maybe : this.options.maybe,
			awaiting : this.options.awaiting,
			rsvpRejected : attendance.rsvpRejected
		}));
	}
	
	this.displayConfirmation = function(msg) {
		if (msg) {
			$j('<p />').html(msg).message({style: 'success' });
		}
	}
	
	this.displayAllAttendees = function(attendees) {
		$j('#jive-event-all-attendees').html(jive.event.attendee.allAttendees({
			attendees : attendees.users,
			moreAvailable : attendees.moreAvailable
		}));
		
		$j('#jive-event-all-attendees').popover({context: $j(this), destroyOnClose: false});
	}
	
});
;
